mapper.select:
/* 녹취정보 조회 selectItem */ select top 1 rec_seq ,convert(varchar(19),rec_datm,121) as rec_datm ,rec_date ,convert(varchar(8),rec_start_time,108) as rec_start_time ,convert(varchar(8),rec_end_time,108) as rec_end_time ,convert(varchar(8),rec_call_time,108) as rec_call_time ,system_code ,business_code ,local_no ,channel_no ,rec_keycode ,rec_inout ,rec_filename ,user_id ,user_name ,bpart_code ,bpart_name ,mpart_code ,mpart_name ,spart_code ,spart_name ,isnull(cust_id,'') as cust_id ,isnull(cust_name,'') as cust_name ,isnull(cust_tel,'') as cust_tel ,isnull(cust_jumin,'') as cust_jumin ,custom_fld_04 as guideTel ,web_url from tbl_record WITH(NOLOCK) where 1=1
and rec_seq = #{rec_seq}
and rec_date = #{rec_date}
and local_no = #{local_no}
and rec_keycode = #{rec_keycode}
and rec_filename = #{rec_filename}
and user_id = #{user_id}

/* 녹취이력 count selectCount */
select 
	count(*) as tot_cnt
	,CEILING(CAST(COUNT(*) as float) / ${top_cnt}) as page_cnt
from tbl_record A WITH(NOLOCK)
where rec_datm between convert(datetime, #{rec_date1} + ' 00:00:00.000') and convert(datetime, #{rec_date2} + ' 23:59:59.997')
and rec_start_time between #{rec_start_time1} and #{rec_start_time2}
and rec_call_time between #{rec_call_time1} and #{rec_call_time2}

	and user_id = #{user_id}


	and cust_name like #{cust_name} + '%'

/* 녹취이력 조회 selectList */ select (${tot_cnt}-rownum+1) idx, * from ( select top ${end_cnt} ROW_NUMBER() OVER(order by ${sortMethod}) as rownum ,A.rec_seq ,A.custom_fld_04 ,convert(varchar(19), A.rec_datm, 121) as rec_datm ,A.rec_date ,convert(varchar(8), A.rec_start_time, 108) as rec_start_time ,convert(varchar(8),A.rec_end_time, 108) as rec_end_time ,convert(varchar(8),A.rec_call_time,108) as rec_call_time ,A.system_code ,A.business_code ,A.local_no ,A.channel_no ,A.rec_keycode ,A.rec_inout ,A.rec_filename ,A.user_id ,A.user_name ,isnull(A.cust_id, '') as cust_id ,isnull(A.cust_name, '') as cust_name ,isnull(A.cust_tel, '') as cust_tel ,isnull(A.cust_jumin, '') as cust_jumin ,A.web_url ,isNull(B.eval_status, 'x') as eval_status ,isNull(B.eval_order, '0') as eval_order from tbl_record A WITH(NOLOCK) left outer join tbl_eval_event_result_list B WITH(NOLOCK) on A.rec_seq = B.rec_seq and B.event_code = #{event_code} and B.eval_user_id = #{_eval_user_id} where A.rec_datm between convert(datetime, #{rec_date1} + ' 00:00:00.000') and convert(datetime, #{rec_date2} + ' 23:59:59.997') and A.rec_start_time between #{rec_start_time1} and #{rec_start_time2} and A.rec_call_time between #{rec_call_time1} and #{rec_call_time2}
and A.user_id = #{user_id}
and A.cust_name like #{cust_name} + '%'
) U where rownum between ${start_cnt} and ${end_cnt}
