mapper.select:
/* 청취 이력 조회 selectList */ select (${tot_cnt}-rnum+1) as idx ,X.* from ( select rownum as rnum ,XX.* from ( select to_char(tlh.listen_datm, 'YYYY-MM-DD HH24:MI:SS') as listen_datm ,tlh.listen_id ,tlh.listen_name ,tlh.listen_ip ,to_char(tlh.rec_datm, 'YYYY-MM-DD HH24:MI:SS') as rec_datm ,tlh.rec_keycode ,tlh.rec_filename ,tlh.user_id ,tlh.user_name ,tlh.local_no ,'link' as v_url ,nvl(tlh.reason_code, '') as reason_code ,nvl(tlh.reason_text, '') as reason_text ,(case when nvl(tlh.reason_code, '') != '' then fn_GetCodeName('LD_REASON', tlh.reason_code) else '' end) as reason_code_desc ,(select part_name from tbl_user_group where part_depth = 1 and bpart_code = tu.bpart_code) as bpart_name ,(select part_name from tbl_user_group where part_depth = 2 and bpart_code = tu.bpart_code and mpart_code = tu.mpart_code) as mpart_name ,(select part_name from tbl_user_group where part_depth = 3 and bpart_code = tu.bpart_code and mpart_code = tu.mpart_code and spart_code = tu.spart_code) as spart_name from tbl_listen_hist tlh left join tbl_user tu on tlh.listen_id = tu.user_id where 1=1 and tlh.listen_datm between to_date(#{listen_date1} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS') and to_date(#{listen_date2} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
and tlh.listen_id like #{listen_id} || '%'
and tlh.listen_name like #{listen_name} || '%'
and tlh.rec_datm between to_date(#{rec_date1} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS') and to_date(#{rec_date2} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
and tlh.user_id like #{user_id} || '%'
and tlh.user_name like #{user_name} || '%'
and tlh.reason_code like #{reason_code} || '%'
order by tlh.${sort_idx} ${sort_dir}
) XX
where rownum <= ${end_cnt} ) X where X.rnum >= ${start_cnt}

mapper.insert:
insert into tbl_listen_hist	/* 청취 이력 저장 insertListenHist */ 
(
	listen_datm
	,listen_id
	,listen_name
	,listen_ip
	,rec_datm
	,rec_keycode
	,rec_filename
	,user_id
	,user_name
	,local_no
	,reason_code
	,reason_text
	,listen_src
) 
values 
(
	sysdate
	,#{login_id}
	,#{login_name}
	,#{listen_ip}
	,to_date(#{rec_datm}, 'YYYY-MM-DD HH24:MI:SS')
	,#{rec_keycode}
	,#{rec_filename}
	,#{user_id}
	,#{user_name}
	,#{local_no}
	,#{reason_code}
	,#{reason_text}
	,#{listen_src}
)
