mapper.select:
/* 사용자 변경 이력 count selectCount */ select count(*) as tot_cnt ,ceil(count(*) / ${top_cnt}) as page_cnt from tbl_user_change_hist where 1=1 and change_datm between to_date(#{change_date1} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS') and to_date(#{change_date2} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
and change_id like #{change_id} || '%'
and change_name like #{change_name} || '%'
and user_id like #{user_id} || '%'
and user_name like #{user_name} || '%'
and change_type = #{change_type}

/* 사용자 변경 이력 조회 selectList */ select (${tot_cnt}-rnum+1) as idx ,X.* from ( select rownum as rnum ,XX.* from ( select to_char(change_datm, 'YYYY-MM-DD HH24:MI:SS') as change_datm ,change_type ,(case change_type when 'I' then '등록' when 'U' then '수정' when 'D' then '삭제' when 'L' then '잠금해제' else change_type end) as v_change_type ,user_id ,user_name ,change_id ,change_name ,change_ip ,nvl(origin_level, '') as origin_level ,nvl(change_level, '') as change_level ,fn_GetCodeName('USER_LEVEL', origin_level) as origin_level_desc ,fn_GetCodeName('USER_LEVEL', change_level) as change_level_desc from tbl_user_change_hist where 1=1 and change_datm between to_date(#{change_date1} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS') and to_date(#{change_date2} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
and change_id like #{change_id} || '%'
and change_name like #{change_name} || '%'
and user_id like #{user_id} || '%'
and user_name like #{user_name} || '%'
and change_type = #{change_type}
order by ${sort_idx} ${sort_dir} ) XX
where rownum <= ${end_cnt} ) X where X.rnum >= ${start_cnt}

/* 사용자 변경 이력 전체 조회 (엑셀다운로드) selectListAll */ select to_char(change_datm, 'YYYY-MM-DD HH24:MI:SS') as change_datm ,change_type ,(case change_type when 'I' then '등록' when 'U' then '수정' when 'D' then '삭제' else change_type end) as v_change_type ,user_id ,user_name ,change_id ,change_name ,change_ip ,nvl(origin_level, '') as origin_level ,nvl(change_level, '') as change_level ,fn_GetCodeName('USER_LEVEL', origin_level) as origin_level_desc ,fn_GetCodeName('USER_LEVEL', change_level) as change_level_desc from tbl_user_change_hist where 1=1 and change_datm between to_date(#{change_date1} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS') and to_date(#{change_date2} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
and change_id like #{change_id} || '%'
and change_name like #{change_name} || '%'
and user_id like #{user_id} || '%'
and user_name like #{user_name} || '%'
and change_type = #{change_type}
order by ${sort_idx} ${sort_dir}

mapper.insert:
insert into tbl_user_change_hist	/* 사용자 변경 이력 저장 insertUserChangeHist */ 
(
	change_datm
	,change_type
	,user_id
	,user_name
	,change_id
	,change_name
	,change_ip
	,origin_level
	,change_level
)
values 
(
	sysdate
	,#{change_type}
	,#{user_id}
	,#{user_name}
	,#{change_id}
	,#{change_name}
	,#{change_ip}
	,#{origin_level}
	,#{change_level}
)
