mapper.select:
/* 결과 권한 체크 selectResultPerm */
select 
	(case when sum(cnt) > 0 then '1' 
		else '0' end) as perm_yn
from (
	select count(*) cnt
	from tbl_search_config
	where conf_type='R' 
	and conf_field = #{conf_field} 
	and user_level >= #{user_level}
	union all
	select count(*) cnt 
	from tbl_search_user_config
	where conf_type='R' 
	and user_id = #{user_id}
	and conf_code = (select conf_code from tbl_search_config 
						where conf_type='R' 
						and conf_field = #{conf_field})
) U

/* 조회/결과 설정 목록 selectList */ select (business_code || conf_code) as row_id ,business_code ,conf_code ,user_level ,conf_name ,conf_field ,conf_value ,default_used ,order_used ,order_no ,conf_etc ,use_yn ,(select business_name from tbl_business where business_code = A.business_code) as business_name ,fn_GetCodeName('USER_LEVEL', user_level) as user_level_desc from tbl_search_config A where conf_type = #{conf_type}
and business_code = #{business_code}
and user_level >= #{user_level}
and conf_value != #{not_conf_value}
order by order_no asc

mapper.insert:
/* 조회/결과 설정 등록 insertSearchConfig */
insert into tbl_search_config (
	business_code,conf_code,user_level,conf_type,conf_name,conf_field,conf_value,default_used,order_used,order_no,conf_etc,use_yn
)
select 
	#{business_code}
	,(coalesce(max(conf_code::int)
	,(case '${conf_type}' when 'S' then 1000 
		else 2000 end))+1)::varchar
	,#{user_level}
	,#{conf_type}
	,#{conf_name}
	,#{conf_field}
	,#{conf_value}
	,#{default_used}
	,#{order_used}
	,(coalesce(max(order_no),0)+1)
	,#{conf_etc}
	,#{use_yn}
from tbl_search_config
where business_code = #{business_code} 
and conf_type = #{conf_type}

/* 사용자별 조회/결과 설정 등록 insertSearchUserConfig */
insert into tbl_search_user_config (
	user_id,business_code,conf_code,conf_type,order_no
) values (
	#{user_id}, #{business_code}, #{conf_code}, #{conf_type}, #{order_no}::smallint
)

mapper.update:
/* 조회/결과 설정 업데이트 updateSearchConfig */ update tbl_search_config
user_level = #{user_level}, conf_name = #{conf_name}, conf_field = #{conf_field}, conf_value = #{conf_value}, default_used = #{default_used}, order_used = #{order_used}, order_no = #{order_no}::smallint, conf_etc = #{conf_etc}, use_yn = #{use_yn},
where business_code = #{business_code} and conf_code = #{conf_code} and conf_type = #{conf_type}

mapper.delete:
/* 조회/결과 설정 삭제 deleteSearchConfig */
delete from tbl_search_config
where business_code = #{business_code} 
and conf_code = #{conf_code} 
and conf_type = #{conf_type}

/* 사용자별 조회/결과 설정 삭제 deleteSearchUserConfig */
delete from tbl_search_user_config
where user_id = #{user_id} 
and business_code = #{business_code}

and conf_code = #{conf_code}


and conf_type = #{conf_type}
