mapper.select:
/* 그룹 중복 체크 userGroupChk */
select count(*)::int as cnt 
from tbl_user_group
where business_code = #{business_code}

	and bpart_code = #{part_code}
	and mpart_code = #{part_code}
	and spart_code = #{part_code}

/* 연관된 그룹 또는 사용자 존재유무 체크 selectRelationUserGroup */
select sum(cnt) cnt
from (
	select count(*) cnt


			from tbl_user_group
			where bpart_code = #{bpart_code} 
			and part_depth = 2


			from tbl_user_group
			where bpart_code = #{bpart_code} 
			and mpart_code = #{mpart_code} 
			and part_depth = 3


			from tbl_user_group A 
			inner join tbl_user B on A.bpart_code = A.bpart_code 
								and A.mpart_code = B.mpart_code 
								and A.spart_code = B.spart_code
			where A.bpart_code = #{bpart_code} 
			and A.mpart_code = #{mpart_code} 
			and A.spart_code = #{spart_code}


) U

/* 사용자 그룹 목록 조회 selectList */ select (bpart_code || mpart_code || spart_code) as row_id ,bpart_code ,mpart_code ,spart_code ,part_depth ,part_name ,delete_day ,coalesce(part_etc,'') as part_etc ,use_yn
,bpart_code as part_code ,mpart_code as part_code ,spart_code as part_code
from tbl_user_group where 1=1
and use_yn = #{use_yn}
and business_code = #{business_code}
and bpart_code = #{bpart_code}
and mpart_code = #{mpart_code}
and spart_code = #{spart_code}
and part_depth = #{part_depth}::smallint
and ( (bpart_code = #{_bpart_code} and mpart_code = #{_default_code} and spart_code = #{_default_code}) or (bpart_code = #{_bpart_code} and mpart_code = #{_mpart_code}) )
and ( (bpart_code = #{_bpart_code} and mpart_code = #{_default_code} and spart_code = #{_default_code}) or (bpart_code = #{_bpart_code} and mpart_code = #{_mpart_code} and spart_code = #{_default_code}) or (bpart_code = #{_bpart_code} and mpart_code = #{_mpart_code} and spart_code = #{_spart_code}) )
order by row_id asc

/* 사용자 조직도 목록 조회 selectPartCode */ select (bpart_code || mpart_code || spart_code) as row_id ,bpart_code ,mpart_code ,spart_code ,part_depth ,part_name ,coalesce(part_etc, '') as part_etc ,use_yn
,bpart_code as part_code ,mpart_code as part_code ,spart_code as part_code
from tbl_user_group where use_yn = '1' and business_code = #{business_code} and part_depth = #{part_depth}::smallint
and bpart_code = #{bpart_code}
and mpart_code = #{mpart_code}
and spart_code = #{spart_code}
order by row_id asc

mapper.insert:
/* 사용자 그룹 자동 등록 insertUserGroupAuto */
insert into tbl_user_group (
	business_code,bpart_code,mpart_code,spart_code,part_depth,part_name,use_yn
)
select #{business_code},


		(coalesce(max(bpart_code::int), 0)+('1' || substring('${_default_code}', 1, length('${_default_code}')-1))::int)::varchar,
		#{bpart_code},


		'${_default_code}',
		(coalesce(max(mpart_code::int), #{bpart_code})+100)::varchar,
		#{mpart_code},


		(coalesce(max(spart_code::int), #{mpart_code})+1)::varchar,
		'${_default_code}',


	#{part_depth}::smallint,
	#{part_name},
	'1'
from tbl_user_group
where business_code = #{business_code} 
and part_depth = #{part_depth}

and bpart_code = #{bpart_code}


and mpart_code = #{mpart_code}

mapper.update:
/* 사용자 그룹 업데이트 updateUserGroup */
update tbl_user_group 
	set part_name = #{part_name}

	,delete_day = #{delete_day}::int

where bpart_code = #{bpart_code} 
and mpart_code = #{mpart_code} 
and spart_code = #{spart_code}

mapper.delete:
/* 사용자 그룹 삭제 deleteUserGroup */
delete from tbl_user_group
where bpart_code = #{bpart_code} 
and mpart_code = #{mpart_code} 
and spart_code = #{spart_code}
