mapper.select:
/* 검색조건 설정 조회 selectSearchConfig */ select conf_code ,conf_name ,conf_field ,conf_value ,default_used ,selected ,conf_etc ,(case conf_value when 'part_code' then 0 else order_no end) as order_no from ( select conf_code ,conf_name ,conf_field ,conf_value ,default_used ,default_used as selected ,coalesce(conf_etc,'') as conf_etc ,order_no from tbl_search_config A where business_code = #{business_code} and conf_type = 'S'
and default_used = #{default_used}
and use_yn = '1'
and user_level >= #{user_level} and (select count(*) from tbl_search_user_config where user_id = #{user_id} and business_code = #{business_code} and conf_code = A.conf_code and conf_type = A.conf_type) < 1
union all select A.conf_code ,B.conf_name ,B.conf_field ,B.conf_value ,B.default_used ,'1' as selected ,coalesce(B.conf_etc,'') as conf_etc ,A.order_no from tbl_search_user_config A inner join tbl_search_config B on A.business_code = B.business_code and A.conf_code = B.conf_code and A.conf_type = B.conf_type where A.user_id = #{user_id} and A.business_code = #{business_code} and A.conf_type = 'S' and B.use_yn = '1'
and B.user_level >= #{user_level}
) U order by order_no asc

/* 목록 필드 설정 조회 selectResultConfig */ select * from ( select conf_code ,conf_name ,conf_field ,order_used ,default_used ,default_used as selected ,coalesce(conf_etc,'') as conf_etc ,'0' as order_no ,conf_field as field_value ,conf_value as result_value from tbl_search_config where business_code = #{business_code} and conf_type = 'R' and conf_value = 'hidden' and use_yn = '1' union all select conf_code ,conf_name ,conf_field ,order_used ,default_used ,default_used as selected ,coalesce(conf_etc,'') as conf_etc ,order_no , conf_field as field_value , (case when conf_value='normal' then 'n_' || conf_field else 'v_' || conf_value end) as result_value from tbl_search_config A where business_code = #{business_code} and conf_type = 'R'
and default_used = #{default_used}
and conf_value!='hidden' and use_yn = '1'
and user_level >= #{user_level} and (select count(*) from tbl_search_user_config where user_id = #{user_id} and business_code = #{business_code} and conf_code = A.conf_code and conf_type = A.conf_type) < 1
union all select A.conf_code ,B.conf_name ,B.conf_field ,B.order_used ,B.default_used ,'1' as selected ,coalesce(B.conf_etc,'') as conf_etc ,A.order_no ,B.conf_field as field_value ,(case when B.conf_value='normal' then 'n_' || B.conf_field else 'v_' || B.conf_value end) as result_value from tbl_search_user_config A inner join tbl_search_config B on A.business_code = B.business_code and A.conf_code = B.conf_code and A.conf_type = B.conf_type where A.user_id = #{user_id} and A.business_code = #{business_code} and A.conf_type = 'R' and B.conf_value != 'hidden' and B.use_yn = '1'
and B.user_level >= #{user_level}
) U order by order_no asc

/* 연관 녹취이력 조회 selectRelationList */
select 
	rec_seq
	,to_char(rec_datm, 'YYYY-MM-DD HH24:MI:SS') as rec_datm
	,rec_date
	,to_char(rec_start_time, 'HH24:MI:SS') as rec_start_time
	,to_char(rec_call_time, 'HH24:MI:SS') as rec_call_time
	,system_code
	,rec_filename
	,rec_keycode
	,local_no
	,user_id
	,user_name
	,coalesce(rec_store_code,'0') as rec_store_code
	,web_url
from tbl_record
where 1=1
and rec_datm between (#{rec_date} || ' 00:00:00.000')::timestamp and (#{rec_date} || ' 23:59:59.997')::timestamp
and rec_keycode = #{rec_keycode}



		and bpart_code = #{_bpart_code}


		and bpart_code = #{_bpart_code} 
		and mpart_code = #{_mpart_code}


		and bpart_code = #{_bpart_code} 
		and mpart_code = #{_mpart_code} 
		and spart_code = #{_spart_code}


		and user_id = #{_user_id}



order by rec_datm asc, rec_start_time asc

/* 단일 녹취이력 조회 selectItem */ select to_char(rec_datm, 'YYYY-MM-DD HH24:MI:SS') as rec_datm ,rec_date ,to_char(rec_start_time, 'HH24:MI:SS') as rec_start_time ,to_char(rec_end_time, 'HH24:MI:SS') as rec_end_time ,to_char(rec_call_time, 'HH24:MI:SS') as rec_call_time ,system_code ,company_code ,business_code ,local_no ,channel_no ,rec_keycode ,rec_inout ,rec_filename ,user_id ,user_name ,cust_id ,cust_name ,cust_tel ,cust_jumin ,web_url ,coalesce(rec_enc_code,'0') rec_enc_code ,coalesce(rec_store_code,'0') rec_store_code ,coalesce((select system_ip from tbl_system where system_code = tbl_record.system_code),'') as system_ip from tbl_record where 1=1
and rec_datm = #{rec_datm}::timestamp
and rec_date = #{rec_date}
and local_no = #{local_no}
and rec_keycode = #{rec_keycode}
and rec_filename = #{rec_filename}
limit 1

/* 녹취이력 count selectCount */
select 
	count(*)::int as tot_cnt
	,ceil(count(*) / ${top_cnt}::float) as page_cnt
from tbl_record
where 1=1
and rec_datm between (#{rec_date1} || ' 00:00:00.000')::timestamp and (#{rec_date2} || ' 23:59:59.997')::timestamp
and rec_start_time between #{rec_start_time1}::time and #{rec_start_time2}::time
and rec_call_time between #{rec_call_time1}::time and #{rec_call_time2}::time

and rec_succ_code = #{rec_succ_code}


and system_code = #{system_code}



		and local_no between #{local_no1} and #{local_no2}


		and local_no like #{local_no1} || '%'


		and local_no like #{local_no2} || '%'



and bpart_code = #{bpart_code}


and mpart_code = #{mpart_code}


and spart_code = #{spart_code}


and rec_inout = #{rec_inout}


and user_id like #{user_id} || '%'


and user_name like #{user_name} || '%'


and cust_id like #{cust_id} || '%'


and cust_name like #{cust_name} || '%'


and cust_tel like '%' || #{cust_tel} || '%'


and rec_keycode = #{rec_keycode}


and rec_filename like #{rec_filename} || '%'


and rec_abort_code = #{rec_abort_code}


and rec_store_code = #{rec_store_code}


and rec_mystery_code = #{rec_mystery_code}


and user_id in (${user_list})


and custom_fld_01 = #{custom_fld_01}


and custom_fld_02 = #{custom_fld_02}


and custom_fld_03 = #{custom_fld_03}


and custom_fld_04 = #{custom_fld_04}


and custom_fld_05 = #{custom_fld_05}




			and bpart_code = #{_bpart_code}


			and bpart_code = #{_bpart_code} 
			and mpart_code = #{_mpart_code}


			and bpart_code = #{_bpart_code} 
			and mpart_code = #{_mpart_code} 
			and spart_code = #{_spart_code}


			and user_id = #{_user_id}

/* 녹취이력 조회 selectList */
select 
	(${s.tot_cnt}-rownum+1) as idx
	,*
from (
	select
		ROW_NUMBER() OVER(order by ${s.sort_idx} ${s.sort_dir}) as rownum
		,rec_seq
		,to_char(rec_datm, 'YYYY-MM-DD HH24:MI:SS') as rec_datm
		,rec_keycode
		,rec_filename
		,local_no
		,system_code
		,business_code
		,bpart_code
		,mpart_code
		,spart_code
		,to_char(rec_call_time, 'HH24:MI:SS') as rec_call_time
		,user_name
		,cust_id
		,custom_fld_01
		,custom_fld_02
		,custom_fld_03
		,custom_fld_04
		,custom_fld_05
		,rec_stt_code



				to_char(${item}, 'YYYYMMDD') as ${idx}


				to_char(${item}, 'HH24:MI:SS') as ${idx}


				to_char(${item}, 'HH24:MI:SS') as ${idx}


				to_char(${item}, 'HH24:MI:SS') as ${idx}


				(case ${item} when 'I' then '인' 
							  when 'O' then '아웃' 
							  when 'L' then '내선' 
					else ${item} end) as ${idx}


				(case ${item} when '1' then '성공' 
							  when '0' then '실패' 
					else ${item} end) as ${idx}


				(case ${item} when '0' then '대기' 
							  when '1' then '성공' 
							  when '2' then '실패' 
					else ${item} end) as ${idx}


				${item} as ${idx}


				'link' as ${idx}


				'down' as ${idx}


				coalesce(memo_cnt,0) as ${idx}


				${item} as ${idx}



	from tbl_record
	where 1=1
	and rec_datm between (#{s.rec_date1} || ' 00:00:00.000')::timestamp and (#{s.rec_date2} || ' 23:59:59.997')::timestamp
	and rec_start_time between #{s.rec_start_time1}::time and #{s.rec_start_time2}::time
	and rec_call_time between #{s.rec_call_time1}::time and #{s.rec_call_time2}::time

	and rec_succ_code = #{s.rec_succ_code}


	and system_code = #{s.system_code}



			and local_no between #{s.local_no1} and #{s.local_no2}


			and local_no like #{s.local_no1} || '%'


			and local_no like #{s.local_no2} || '%'



	and bpart_code = #{s.bpart_code}


	and mpart_code = #{s.mpart_code}


	and spart_code = #{s.spart_code}


	and rec_inout = #{s.rec_inout}


	and user_id like '%' || #{s.user_id} || '%'


	and user_name like '%' || #{s.user_name} || '%'


	and cust_id like '%' || #{s.cust_id} || '%'


	and cust_name like '%' || #{s.cust_name} || '%'


	and cust_tel like '%' ||  #{s.cust_tel} || '%'



	and rec_keycode = #{s.rec_keycode}


	and rec_filename like #{s.rec_filename} || '%'


	and rec_abort_code = #{s.rec_abort_code}


	and rec_store_code = #{s.rec_store_code}


	and rec_mystery_code = #{s.rec_mystery_code}


	and user_id in (${s.user_list})


	and custom_fld_01 = #{s.custom_fld_01}


 	and custom_fld_02 = #{s.custom_fld_02}


	and custom_fld_03 = #{s.custom_fld_03}


 	and custom_fld_04 like '%' || #{s.custom_fld_04} || '%'


 	and custom_fld_05 = #{s.custom_fld_05}




			and bpart_code = #{s._bpart_code}


			and bpart_code = #{s._bpart_code} 
			and mpart_code = #{s._mpart_code}


			and bpart_code = #{s._bpart_code} 
			and mpart_code = #{s._mpart_code} 
			and spart_code = #{s._spart_code}


			and user_id = #{s._user_id}



	order by ${s.sort_idx} ${s.sort_dir}
	limit ${s.top_cnt} offset (${s.start_cnt}-1)
) U

mapper.update:
/* 고객 정보 누락 업데이트 updateOmission */
update tbl_record

	cust_id = #{cust_id}
	,rec_keycode = #{rec_keycode}
	,custom_fld_02 = #{custom_fld_02}
	,custom_fld_03 = #{custom_fld_03}
	,custom_fld_04 = #{custom_fld_04}

where 1=1
and rec_datm = #{rec_datm}::timestamp
and local_no = #{local_no}
and rec_filename = #{rec_filename}

/* 필수중단 업데이트 updateAbortCode */
update tbl_record
	set rec_abort_code = #{rec_abort_code}
where (rec_date = #{rec_date2} and rec_start_time between #{rec_start_time}::time and #{rec_end_time}::time)
   or (rec_date = #{rec_date2} and rec_end_time between #{rec_start_time}::time and #{rec_end_time}::time)
