mapper.select:
/* 배정상담원 단일조회 selectItem */
select 
	user_name
	,eval_cnt
	,eval_status
from tbl_eval_event_agent_list WITH(NOLOCK)
where user_id = #{user_id} 
and eval_user_id = #{eval_user_id}

/* 평가대상자관리 - 가용상담원조회 selectAvailList */ select A.user_id ,A.user_name ,A.local_no ,A.bpart_code ,A.mpart_code ,A.spart_code ,J1.part_name as bpart_name ,J2.part_name as mpart_name ,J3.part_name as spart_name from tbl_user A WITH(NOLOCK) ,tbl_user_group J1 WITH(NOLOCK) ,tbl_user_group J2 WITH(NOLOCK) ,tbl_user_group J3 WITH(NOLOCK) where A.eval_yn = 'n' and A.user_id not in (select user_id from tbl_eval_event_agent_list EA WITH(NOLOCK)) and A.bpart_code = J1.bpart_code and J1.part_depth = 1 and A.bpart_code = J2.bpart_code and A.mpart_code = J2.mpart_code and J2.part_depth = 2 and A.bpart_code = J3.bpart_code and A.mpart_code = J3.mpart_code and A.spart_code = J3.spart_code and J3.part_depth = 3
and A.bpart_code = #{bpart_code}
and A.mpart_code = #{mpart_code}
and A.spart_code = #{spart_code}
order by A.bpart_code, A.mpart_code, A.spart_code, A.user_name asc
order by A.user_name asc, A.bpart_code, A.mpart_code, A.spart_code
order by A.user_name desc, A.bpart_code, A.mpart_code, A.spart_code

/* 평가대상자관리 - 배정상담원조회 selectAssignList */ select A.user_id ,A.user_name ,A.local_no ,A.bpart_code ,A.mpart_code ,A.spart_code ,J1.part_name as bpart_name ,J2.part_name as mpart_name ,J3.part_name as spart_name ,isNull(E.eval_status, 'x') as eval_status from tbl_user A WITH(NOLOCK) left outer join tbl_eval_event_result_list E WITH(NOLOCK) on E.event_code = #{event_code} and A.user_id = E.user_id and E.eval_order = #{eval_order} ,tbl_eval_event_agent_list D WITH(NOLOCK) ,tbl_user_group J1 WITH(NOLOCK) ,tbl_user_group J2 WITH(NOLOCK) ,tbl_user_group J3 WITH(NOLOCK) where A.eval_yn = 'n' and A.bpart_code=J1.bpart_code and J1.part_depth=1 and A.bpart_code=J2.bpart_code and A.mpart_code=J2.mpart_code and J2.part_depth=2 and A.bpart_code=J3.bpart_code and A.mpart_code=J3.mpart_code and A.spart_code=J3.spart_code and J3.part_depth=3 and A.user_id=D.user_id and D.eval_user_id=#{eval_user_id}
and (A.user_id = #{user_name} or A.user_name like #{user_name}+'%')
and D.eval_user_id=#{_eval_user_id}
order by A.bpart_code, A.mpart_code, A.spart_code, A.user_name asc

mapper.delete:
/* 평가대상자관리 - 배정상담원 삭제 deleteEventAgentList */
delete from tbl_eval_event_agent_list
where user_id = #{user_id} 
and eval_user_id = #{eval_user_id}
