mapper.select:
/* 모니터링 리스트 조회 selectList */ select A.state ,A.channel ,A.system_code ,A.login ,A.user_id ,A.user_name ,A.bpart_code ,A.mpart_code ,A.spart_code ,A.ani ,A.phone ,A.datetime ,convert(varchar,dateadd(s, DATEDIFF(s, cast(A.datetime as time), cast(GETDATE() as time)), ''),108) as call_time ,B.system_ip ,isnull(B.backup_ip,'') as backup_ip ,A.inout as rec_inout ,(select part_name from tbl_user_group where part_depth = 3 and bpart_code = A.bpart_code and mpart_code = A.mpart_code and spart_code = A.spart_code) as spart_name from crec_monitoring A WITH(NOLOCK) inner join tbl_system B WITH(NOLOCK) on A.system_code = B.system_code where 1=1
and A.bpart_code = #{bpart_code}
and A.mpart_code = #{mpart_code}
and A.spart_code = #{spart_code}
and A.phone = #{local_no}
and A.bpart_code = #{_bpart_code}
and A.bpart_code = #{_bpart_code} and A.mpart_code = #{_mpart_code}
and A.bpart_code = #{_bpart_code} and A.mpart_code = #{_mpart_code} and A.spart_code = #{_spart_code}
and A.user_id = #{_user_id}
order by
A.state desc, A.inout asc
A.state desc, A.inout desc
A.state desc, A.datetime desc
A.phone asc
A.user_name asc
cast(A.channel as int) asc

/* 녹취전체 건수 및 진행중 건수 조회 selectCountList */
select
	 (select count(*) as rec_count 
	 	from tbl_record WITH(NOLOCK)
	 	where rec_date = convert(varchar(8), getdate(), 112)) as rec_count
	,(select count(*) as mon_count 
		from crec_monitoring WITH(NOLOCK) 
		where state='1') as mon_count
	,convert(varchar, getdate(), 20) as time
