mapper.select:
/* 다운로드 이력 조회 selectList */ select (#{tot_cnt}-rnum+1) as idx ,X.* from ( select rownum as rnum ,XX.* from ( select to_char(tdh.down_datm, 'YYYY-MM-DD HH24:MI:SS') as down_datm ,tdh.down_id ,tdh.down_name ,tdh.down_ip ,to_char(tdh.rec_datm, 'YYYY-MM-DD HH24:MI:SS') as rec_datm ,tdh.rec_keycode ,tdh.rec_filename ,tdh.user_id ,tdh.user_name ,tdh.local_no ,nvl(tdh.reason_code, '') as reason_code ,nvl(tdh.reason_text, '') as reason_text ,(case tdh.down_src when 'AP' then 'TA' else '' end) as down_src ,(case when nvl(tdh.reason_code, ' ') != ' ' then fn_GetCodeName('LD_REASON',tdh.reason_code) else '' end) as reason_code_desc ,(select part_name from tbl_user_group where part_depth = 1 and bpart_code = tu.bpart_code) as bpart_name ,(select part_name from tbl_user_group where part_depth = 2 and bpart_code = tu.bpart_code and mpart_code = tu.mpart_code) as mpart_name ,(select part_name from tbl_user_group where part_depth = 3 and bpart_code = tu.bpart_code and mpart_code = tu.mpart_code and spart_code = tu.spart_code) as spart_name from tbl_down_hist tdh left join tbl_user tu on tdh.down_id = tu.user_id where 1=1 and tdh.down_datm between to_date(#{down_date1} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS') and to_date(#{down_date2} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
and tdh.down_id like #{down_id} || '%'
and tdh.down_name like #{down_name} || '%'
and tdh.rec_datm between to_date(#{rec_date1} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS') and to_date(#{rec_date2} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
and tdh.user_id like #{user_id} || '%'
and tdh.user_name like #{user_name} || '%'
and tdh.reason_code like #{reason_code} || '%'
and tdh.down_src != 'AP'
order by ${sort_idx} ${sort_dir}
) XX
where rownum <= #{end_cnt} ) X where X.rnum >= #{start_cnt}

mapper.insert:
insert into tbl_down_hist	/* 다운로드 이력 저장 insertDownHist */
(
	down_datm
	,down_id
	,down_name
	,down_ip
	,rec_datm
	,rec_keycode
	,rec_filename
	,user_id
	,user_name
	,local_no
	,reason_code
	,reason_text
	,down_src
) 
values 
(
	sysdate
	,#{login_id}
	,#{login_name}
	,#{down_ip}
	,to_date(#{rec_datm}, 'YYYY-MM-DD HH24:MI:SS')
	,#{rec_keycode}
	,#{rec_filename}
	,#{user_id}
	,#{user_name}
	,#{local_no}
	,#{reason_code}
	,#{reason_text}
	,#{down_src}
)
