mapper.select:
/* 로그인 이력 count selectCount */ select count(*) as tot_cnt ,ceil(count(*) / ${top_cnt}) as page_cnt from tbl_login_hist where 1=1 and login_datm between to_date(#{login_date1} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS') and to_date(#{login_date2} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
and login_id like #{login_id} || '%'
and login_name like #{login_name} || '%'
and login_type = #{login_type}
and login_result = #{login_result}

/* 로그인 이력 조회 selectList */ select (#{tot_cnt}-rnum+1) as idx ,X.* from ( select rownum as rnum ,XX.* from ( select login_type ,(case login_type when 'I' then '로그인' else '로그아웃' end) as v_login_type ,to_char(login_datm, 'YYYY-MM-DD HH24:MI:SS') as login_datm ,login_id ,login_name ,login_ip ,login_result ,(case login_result when '1' then '성공' else '실패' end) as v_login_result from tbl_login_hist where 1=1 and login_datm between to_date(#{login_date1} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS') and to_date(#{login_date2} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
and login_id like #{login_id} || '%'
and login_name like #{login_name} || '%'
and login_type = #{login_type}
and login_result = #{login_result}
order by ${sort_idx} ${sort_dir}
) XX
where rownum <= #{end_cnt} ) X where X.rnum >= #{start_cnt}

/* 로그인 이력 전체 조회 (엑셀다운로드) selectListAll */ select login_type ,(case login_type when 'I' then '로그인' else '로그아웃' end) as v_login_type , to_char(login_datm, 'YYYY-MM-DD HH24:MI:SS') as login_datm , login_id , login_name , login_ip , login_result ,(case login_result when '1' then '성공' else '실패' end) as v_login_result from tbl_login_hist where 1=1 and login_datm between to_date(#{login_date1} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS') and to_date(#{login_date2} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
and login_id like #{login_id} || '%'
and login_name like #{login_name} || '%'
and login_type = #{login_type}
and login_result = #{login_result}
order by ${sort_idx} ${sort_dir}

mapper.insert:
insert into tbl_login_hist	/* 로그인 이력 저장 insertLoginHist */
(
	login_datm
	,login_id
	,login_name
	,login_ip
	,login_type
	,login_result
) 
values 
(
	sysdate
	,#{login_id}
	,#{login_name}
	,#{login_ip}
	,#{login_type}
	,#{login_result}
)
