mapper.select:
/* 접근가능한 메뉴정보 조회 (로그인) selectMenuPerm */ select menu_code ,parent_code ,menu_name ,menu_depth ,menu_url ,menu_icon ,nvl(menu_etc,'') as menu_etc from tbl_menu where business_code = #{business_code}
and user_level >= #{user_level}
and use_yn = '1' order by order_no asc

/* 메뉴 목록 selectList */ select (tm.business_code || tm.menu_code) as row_id ,tm.business_code ,tm.menu_code ,tm.user_level ,tm.parent_code ,tm.menu_name ,tm.menu_depth ,tm.menu_url ,tm.menu_icon ,nvl(tm.menu_etc, '') as menu_etc ,tm.order_no ,tm.use_yn ,(select business_name from tbl_business where business_code = tm.business_code) as business_name ,(case tm.menu_depth when 1 then '없음' else (select menu_name from tbl_menu where business_code = tm.business_code and menu_code = tm.parent_code) end) as parent_name ,fn_GetCodeName('USER_LEVEL', user_level) as user_level_desc from tbl_menu tm where 1=1
and tm.business_code = #{business_code}
and tm.parent_code = #{parent_code}
and tm.menu_depth = #{menu_depth}
and tm.user_level >= #{user_level}
and tm.use_yn = #{use_yn}
order by tm.business_code asc, tm.order_no asc

mapper.update:
insert into tbl_menu	/* 메뉴 등록 insertMenu */ 
(
	business_code
	,menu_code
	,user_level
	,parent_code
	,menu_name
	,menu_depth
	,menu_url
	,menu_icon
	,menu_etc
	,order_no
	,use_yn
)
select 
	#{business_code}
	,menu_code
	,#{user_level}
	,(case '${parent_code}' when '_parent' then to_char(menu_code) 
		else '${parent_code}' end)
	,#{menu_name}
	,(case '${parent_code}' when '_parent' then 1 
		else 2 end)
	,#{menu_url}
	,#{menu_icon}
	,#{menu_etc}
	,substr(menu_code,1,2) || substr(menu_code,-1)
	,#{use_yn}
from (


			select (nvl(max(menu_code), 0)+1000) as menu_code
			from tbl_menu
			where business_code = #{business_code} 
			and menu_depth = 1


			select (nvl(max(menu_code), '${parent_code}')+1) as menu_code
			from tbl_menu
			where business_code = #{business_code} 
			and parent_code = #{parent_code} 
			and menu_depth = 2


) U

/* 메뉴 업데이트 updateMenu */ update tbl_menu
user_level = #{user_level}, menu_name = #{menu_name}, menu_url = #{menu_url}, menu_icon = #{menu_icon}, menu_etc = #{menu_etc}, order_no = #{order_no}, use_yn = #{use_yn},
where business_code = #{business_code} and menu_code = #{menu_code}

/* 하위 메뉴 비활성화 updateParentMenu */
update tbl_menu

	use_yn = '0'

where business_code = #{business_code}
and parent_code = #{menu_code}

mapper.delete:
/* 메뉴 삭제 (하위 메뉴가 없는 경우만 삭제됨) deleteMenu */
delete from tbl_menu
where business_code = #{business_code} 
and menu_code = #{menu_code} 
and (select menu_code from tbl_menu A 
		where A.parent_code = tbl_menu.menu_code 
		and A.menu_depth = tbl_menu.menu_depth+1
		and rownum = 1
) is null
