mapper.select:
/* 상담원/부서별 통계 조회 selectList */ select * from ( select tt.rec_date,
substr(tt.rec_date, 1, 4) || '-' || substr(tt.rec_date, 5, 2) || '-' || substr(tt.rec_date, 7, 2)
substr(tt.rec_date, 1, 4) || '-' || substr(tt.rec_date, 5, 2)
tt.rec_date
tt.rec_date || '시'
substr(tt.rec_date, 1, 4) || '-' || substr(tt.rec_date, 5, 2) || '-' || substr(tt.rec_date, 7, 1) || '주차'
case tt.rec_date when '1' then '일' when '2' then '월' when '3' then '화' when '4' then '수' when '5' then '목' when '6' then '금' when '7' then '토' else 'X' end
as v_rec_date ,(case tt.user_id when '' then '상담사 정보 없음' else tt.user_id end) as user_id
,nvl(max(user_name), (case user_id when '' then '상담사 정보 없음' else user_id end))
,nvl((select part_name from tbl_user_group where business_code = substr(tt.user_id, 1, 2) and bpart_code = substr(tt.user_id, 3, ${s._part_code_size}) and mpart_code = substr(tt.user_id, 3 + (${s._part_code_size}*1), ${s._part_code_size}) and spart_code = substr(tt.user_id, 3 + (${s._part_code_size}*2), ${s._part_code_size}) and part_depth = 3), '비매칭(' || substr(tt.user_id,-${s._part_code_size}) || ')')
as user_name ,sum(tt.tot_cnt) as tot_cnt ,sum(tt.tot_call_time) as tot_call_time ,round(sum(tt.tot_call_time)/sum(tt.tot_cnt)) as avg_call_time ,sum(tt.in_cnt) as in_cnt ,sum(tt.out_cnt) as out_cnt ,sum(tt.local_cnt) as local_cnt ,sum(tt.one_under_cnt) as one_under_cnt ,sum(tt.one_five_cnt) as one_five_cnt ,sum(tt.five_ten_cnt) as five_ten_cnt ,sum(tt.ten_over_cnt) as ten_over_cnt from ( select
tsr.rec_date
substr(tsr.rec_date, 1, 6)
substr(tsr.rec_date, 1, 4)
tsr.rec_hour
substr(tsr.rec_date,1,6) || to_char(to_date(tsr.rec_date),'w')
to_char(to_date(tsr.rec_date), 'd')
as rec_date
,tsr.user_id
,(tsr.business_code || tsr.bpart_code || tsr.mpart_code || tsr.spart_code)
as user_id ,tsr.tot_cnt ,tsr.tot_call_time ,tsr.in_cnt ,tsr.out_cnt ,tsr.local_cnt ,tsr.one_under_cnt ,tsr.one_five_cnt ,tsr.five_ten_cnt ,tsr.ten_over_cnt ,tb.user_name from tbl_stat_record tsr left join tbl_user tb on tsr.user_id = tb.user_id where tsr.rec_date between #{s.rec_date1} and #{s.rec_date2} and tsr.rec_hour between #{s.rec_hour1} and #{s.rec_hour2}
and tsr.bpart_code = #{s.bpart_code}
and tsr.mpart_code = #{s.mpart_code}
and tsr.spart_code = #{s.spart_code}
and tsr.user_id = #{s.user_id}
and tb.user_name = #{s.user_name}
and tsr.user_id = #{s._user_id}
) tt group by tt.rec_date, tt.user_id ) t order by
${idx} ${item}
